Cheatsheet - LD_PRELOAD trick ## Compilation gcc -o hook.so dlsym.c -ldl -fPIC -shared Usage : LD_PRELOAD=$PWD/hook.so ./target Examples Strcmp hook : #define _GNU_SOURCE #include &lt;stdio.h&gt; #include &lt;dlfcn.h&gt; int strcmp(const char *s1, const char *s2){ printf(&quot;Hook strcmp : strcmp('%s','%s')
&quot;, s1, s2); int (*true_strcmp)(const char*, const char*); true_strcmp = dlsym(RTLD_NEXT, &quot;strcmp&quot;); return true_strcmp(s1,s2); } SSL inspector : #define _GNU_SOURCE #include &lt;stdio.h&gt; #include &lt;unistd.h&gt; #include &lt;dlfcn.h&gt; #include &lt;openssl/ssl.h&gt; int SSL_write(SSL *context, const void *buffer, int size){ int (*orig_ssl_write)(SSL *context, const void *buffer, int size); orig_ssl_write = dlysym(RTLD_NEXT,&quot;SSL_write&quot;); printf(&quot;%s

&quot;, buffer); return orig_ssl_write(context, buffer, size); }
